import React from "react";
import { Button, Card } from "antd";

const PostCard = ({ post }) => {
  return (
    <div>
      <Card
        cover={post.Images[0] && <PostImages images={post.Images} />}
        actions={[]}
      >
        <Image />
        <Content />
        <Buttons></Buttons>
      </Card>
      <CommetForm />
      <Comments />
    </div>
  );
};

export default PostCard;
