import React from "react";
import { Button, Card, Popover } from "antd";
import {
  HeartOutlined,
  MessageOutlined,
  RetweetOutlined,
} from "@ant-design/icons";

const PostCard = ({ post }) => {
  return (
    <div>
      <Card
        cover={post.Images[0] && <PostImages images={post.Images} />}
        actions={[
          <RetweetOutlined />,
          <HeartOutlined />,
          <MessageOutlined />,
          <Popover />,
        ]}
      >
        <Image />
        <Content />
        <Buttons></Buttons>
      </Card>
      <CommetForm />
      <Comments />
    </div>
  );
};

export default PostCard;
