import {
  all,
  fork,
  call,
  take,
  put,
  delay,
  debounce,
  throttle,
  takeLatest,
  takeEvery,
  takeLeading,
  takeMaybe,
} from "redux-saga/effects";
import axios from "axios";

function logInAPI(data) {
  return axios.post("/api/login", data);
}

function* logIn(action) {
  try {
    // const result = yield call(logInAPI, action.data); // call 은 동기, fork 는 비동기
    yield delay(1000);
    yield put({ type: "LOG_IN_SUCCESS", data: result.data });
  } catch (err) {
    yield put({ type: "LOG_IN_FAILURE", data: err.response.data });
  }
}

function logOutAPI() {
  return axios.post("/api/logout");
}

function* logOut() {
  try {
    // const result = yield call(logOutAPI); // call 은 동기, fork 는 비동기
    yield delay(1000);
    yield put({ type: "LOG_OUT_SUCCESS", data: result.data });
  } catch (err) {
    yield put({ type: "LOG_OUT_FAILURE", data: err.response.data });
  }
}

function addPostAPI(data) {
  return axios.post("/api/post", data);
}

function* addPost(action) {
  try {
    // const result = yield call(addPostAPI, action.data); // call 은 동기, fork 는 비동기
    yield delay(1000);
    yield put({ type: "ADD_POST_SUCCESS", data: result.data });
  } catch (err) {
    yield put({ type: "ADD_POST_FAILURE", data: err.response.data });
  }
}

// while(true) 대신 takeEvery 를 사용함
// takeLatest 는 마지막 요청만 실행 (하지만 서버 요청이 2번 감, 응답을 취소하는 것이다.)
// takeLeading 은 첫벗째 요청만 실행


function* watchAddPost() {
  yield throttle("ADD_POST_REQUEST", addPost, 2000);
}

export default function* rootSaga() {
  // fork 와 call 은 다름
  yield all([fork(watchLogIn), fork(watchLogOut), fork(watchAddPost)]);
}
